let sendMessageDefault = (sender_psid, text) => {
    return new Promise(async (resolve, reject) => {
        try {
            let res = {
                "text": text,
                "quick_replies": [
                    {
                        "content_type": "text",
                        "title": "Khám bệnh",
                        "payload": "KHAM_BENH",
                    },
                    {
                        "content_type": "text",
